public string GetEntityColumnData() {    var result = "";    /* Create a query to the City schema, add the Name column to the query. */    var esqResult = new EntitySchemaQuery(UserConnection.EntitySchemaManager, "City");    var colName = esqResult.AddColumn("Name");    /* Run the database query and retrieve the object that has the set identifier. You can retrieve the object UId from the browser navigation bar after opening the section record page. */    var entity = esqResult.GetEntity(UserConnection, new Guid("100B6B13-E8BB-DF11-B00F-001D60E938C6"));    /* Retrieve the object column value. */    result += entity.GetColumnValue(colName.Name).ToString();    return result;}