/* The file content. */var content = new byte[]();/* Create a unique ID for the new file. */Guid recordId = Guid.NewGuid();/* Create the "EntityFileLocator" instance identified by "recordId" and stored in the "ActivityFile" database table. */var fileLocator = new EntityFileLocator("ActivityFile", recordId);/* Pass the created locator to the method of the "UserConnection" class. */IFile file = UserConnection.GetFile(fileLocator);using (Stream stream = file.Read()) {    /* Retrieve the file content and save it to the array. */    content = stream.ReadToEnd();}