public static void DeleteObjectViaWcf(){    /* Create the Creatio app context. */    var context = new Creatio(serverUri);    /* Define the method that adds authentication cookies upon creating a new request. The "AuthenticateExternalRequests()" method authenticates the user and adds the cookies received in response to the data request. */    context.SendingRequest += new EventHandler<SendingRequestEventArgs>(AuthenticateExternalRequests);    /* Select a contact to delete. */    var deleteContact = context.ContactCollection.Where(c => c.Name.Contains("Some user name")).First();    /* Delete the contact. */    context.DeleteObject(deleteContact);    /* Save the changes to Creatio in a single request. */    var responses = context.SaveChanges(SaveChangesOptions.Batch);    /* Implement the custom business logic that handles server responses. */}