FROM mcr.microsoft.com/dotnet/core/sdk:3.1 AS baseEXPOSE 5000 5002RUN apt-get update && \apt-get -y --no-install-recommends install \libgdiplus \libc6-dev && \apt-get clean all && \rm -rf /var/lib/apt/lists/* /var/cache/apt/*WORKDIR /appCOPY . ./FROM base AS finalWORKDIR /appENV ASPNETCORE_ENVIRONMENT DevelopmentENV TZ US/EasternENV COMPlus_ThreadPool_ForceMinWorkerThreads 100ENTRYPOINT ["dotnet", "Terrasoft.WebHost.dll"]