BEGIN	EXECUTE IMMEDIATE 'CREATE GLOBAL TEMPORARY TABLE UsedSysCultures (Id Varchar2(38)) ON COMMIT PRESERVE ROWS';	EXECUTE IMMEDIATE '		INSERT INTO USEDSYSCULTURES (Id) 			SELECT DISTINCT sc."Id" FROM "SysCulture" sc 			INNER JOIN "SysAdminUnit" sau ON sau."SysCultureId" = sc."Id"';	EXECUTE IMMEDIATE '		INSERT INTO "USEDSYSCULTURES" (Id) 			SELECT "SysSettingsValue"."GuidValue" FROM "SysSettingsValue" 			INNER JOIN "SysSettings" ON "SysSettings"."Id" = "SysSettingsValue"."SysSettingsId" 			WHERE "SysSettings"."Code" = ''PrimaryCulture''';	FOR tableNameColumn IN (		SELECT  			a.table_name AS TABLE_NAME,		    a.column_name AS COLUMN_NAME		FROM all_cons_columns a		JOIN all_constraints b ON a.owner = b.owner		                      AND a.constraint_name = b.constraint_name		JOIN all_cons_columns c ON b.r_owner = c.owner		                      AND b.r_constraint_name = c.constraint_name		WHERE b.constraint_type = 'R' 			AND c.table_name = 'SysCulture'			AND c.COLUMN_NAME = 'Id'			AND a.OWNER = (SELECT USER AS OWNER FROM dual))  LOOP    EXECUTE IMMEDIATE 'DELETE FROM "' || tableNameColumn.TABLE_NAME ||	                       '" WHERE "' || tableNameColumn.COLUMN_NAME || '" NOT IN (Select Id from UsedSysCultures)';  END LOOP;	EXECUTE IMMEDIATE 'DELETE FROM "SysCulture" WHERE "Id" NOT IN (SELECT Id FROM USEDSYSCULTURES)';	EXECUTE IMMEDIATE 'TRUNCATE TABLE USEDSYSCULTURES'; 	EXECUTE IMMEDIATE 'DROP TABLE USEDSYSCULTURES';END;