DECLARE  URL_SETTING_ID VARCHAR(38) := NULL;  CONFIG_URL_SETTING_ID VARCHAR(38) := NULL;  IND_API_SETTING_ID VARCHAR(38) := NULL;  URL_VAL_ID VARCHAR(38) := NULL;  CONFIG_URL_VAL_ID VARCHAR(38) := NULL;  IND_API_VAL_ID VARCHAR(38) := NULL;  SYS_ADMIN_UID VARCHAR(38) := '{A29A3BA5-4B0D-DE11-9A51-005056C00008}';  ES_IND VARCHAR(500) := '[specify the URL to the ElasticSearch index, use a string of the following type: http://[SERVER2_IP_ADDRESS]:83/indexname]';  CONFIG_URL VARCHAR(500) := '[specify the URL to the Global Search Service, use a string of the following type: http://SERVER2_IP_ADDRESS:81]';  IND_API_URL VARCHAR(500) := '[specify the URL to the Global Search Indexing Service, use a string of the following type: http://SERVER2_IP_ADDRESS:82]';BEGIN  SELECT "Id" INTO URL_SETTING_ID FROM "SysSettings" WHERE "Code" = 'GlobalSearchUrl';  SELECT "Id" INTO CONFIG_URL_SETTING_ID FROM "SysSettings" WHERE "Code" = 'GlobalSearchConfigServiceUrl';  SELECT "Id" INTO IND_API_SETTING_ID FROM "SysSettings" WHERE "Code" = 'GlobalSearchIndexingApiUrl';  SELECT MAX("Id") INTO URL_VAL_ID  FROM "SysSettingsValue" WHERE "SysSettingsId" = URL_SETTING_ID;  SELECT MAX("Id") INTO CONFIG_URL_VAL_ID  FROM "SysSettingsValue" WHERE "SysSettingsId" = CONFIG_URL_SETTING_ID;  SELECT MAX("Id") INTO IND_API_VAL_ID  FROM "SysSettingsValue" WHERE "SysSettingsId" = IND_API_SETTING_ID;  IF (URL_VAL_ID IS NULL)    THEN      INSERT INTO "SysSettingsValue"        ("SysSettingsId", "SysAdminUnitId", "IsDef", "TextValue")        VALUES        (URL_SETTING_ID, SYS_ADMIN_UID, '1', ES_IND);    ELSE      UPDATE "SysSettingsValue" SET "TextValue" = ES_IND WHERE "SysSettingsId" = URL_SETTING_ID;  END IF;  IF (CONFIG_URL_VAL_ID IS NULL)    THEN      INSERT INTO "SysSettingsValue"        ("SysSettingsId", "SysAdminUnitId", "IsDef", "TextValue")        VALUES        (CONFIG_URL_SETTING_ID, SYS_ADMIN_UID, '1', CONFIG_URL);    ELSE      UPDATE "SysSettingsValue" SET "TextValue" = CONFIG_URL WHERE "SysSettingsId" 